  constructor(accountService, route, apiService, listService) {
    this.accountService = accountService;
    this.route = route;
    this.apiService = apiService;
    this.listService = listService;
    this.noRecordMsg = Constants.NO_RECORDS_FOUND;
    this.settlementID = "";
    this.driverDetail = {
      firstName: "",
      lastName: "",
      paymentDetails: {
        paymentType: ""
      }
    };
    this.settlementData = {
      type: null,
      entityId: null,
      entityName: "",
      setNo: "",
      txnDate: "",
      fromDate: null,
      toDate: null,
      prStart: null,
      prEnd: null,
      tripIds: [],
      tripNames: [],
      trpData: [],
      miles: {
        tripsTotal: 0,
        driverTotal: 0,
        tripsLoaded: 0,
        driverLoaded: 0,
        tripsEmpty: 0,
        driverEmpty: 0,
        tripsTeam: 0,
