        driverLoadedTeam: 0,
        driverEmptyTeam: 0
      },
      addition: [],
      deduction: [],
      additionTotal: 0,
      deductionTotal: 0,
      taxObj: {
        gstPrcnt: 0,
        pstPrcnt: 0,
        hstPrcnt: 0,
        gstAmount: 0,
        pstAmount: 0,
        hstAmount: 0,
        carrLocalTax: 0,
        carrLocalAmount: 0,
        carrFedTax: 0,
        carrFedAmount: 0
      },
      paymentTotal: 0,
      taxes: 0,
      subTotal: 0,
      finalTotal: 0,
      fuelAdd: 0,
      fuelDed: 0,
      status: "unpaid",
      paymentLinked: false,
      pendingPayment: 0,
      currency: "CAD",
      paymentSelected: [],
      fuelIds: [],
      fuelData: [],
      transactionLog: [],
      isFeatEnabled: false
    };
    this.expenses = [];
    this.tripsObj = [];
    this.accounts = [];
    this.operatorDetail = {
      cName: ""
    };
    this.paymentAbr = {
      "ppm": "Pay Per Mile",
      "pp": "Percentage",
      "ppd": "Pay Per Delivery",
      "pph": "Pay Per Hour",
      "pfr": "Pay Flat Rate"
    };
    this.entityName = "";
    this.entityPaymentType = "";
    this.accountsObjects = {};
    this.accountsIntObjects = {};
    this.payments = [];
    this.showModal = true;
    this.selectedFuelEnteries = [];
    this.showDetailBtn = false;
    this.carrierID = "";
  }

  ngOnInit() {
    this.settlementID = this.route.snapshot.params[`settlementID`];
    this.fetchSettlementDetail(); // this.fetchTrips();
    // this.fetchAccountsByIDs();
    // this.fetchAccountsByInternalIDs();

    this.fetchPayments();
  }

  fetchSettlementDetail() {
    this.accountService.getData(`settlement/detail/${this.settlementID}`).subscribe(result => {
      this.settlementData = result[0];
      this.showDetailBtn = true;

      if (!this.settlementData.prStart && !this.settlementData.prEnd) {
        this.settlementData.prStart = this.settlementData.fromDate;
        this.settlementData.prEnd = this.settlementData.toDate;
      }

      if (!this.settlementData.isFeatEnabled) {
        this.fetchAccountsByIDs();
        this.fetchAccountsByInternalIDs();
      }

      this.settlementData.transactionLog.map(v => {
        v.type = v.type.replace("_", " ");
      });

      if (this.settlementData.paymentSelected.length > 0) {
        this.entityPaymentType = this.paymentAbr[this.settlementData.paymentSelected[0].pType];
      }

      this.entityName = this.settlementData.entityName;
      this.fetchSelectedFuelExpenses();
      this.carrierID = result[0].pk;
    });
  }

  fetchAccountsByIDs() {
    this.accountService.getData("chartAc/get/list/all").subscribe(result => {
      this.accountsObjects = result;
    });
  }

  fetchAccountsByInternalIDs() {
    this.accountService.getData("chartAc/get/internalID/list/all").subscribe(result => {
      this.accountsIntObjects = result;
    });
