export class AddAdvancePaymentComponent {
  constructor(listService, route, router, toaster, accountService, apiService, location) {
    this.listService = listService;
    this.route = route;
    this.router = router;
    this.toaster = toaster;
    this.accountService = accountService;
    this.apiService = apiService;
    this.location = location;
    this.paymentData = {
      paymentNo: "",
      advType: null,
      paymentTo: null,
      entityId: null,
      amount: "",
      currency: null,
      payMode: null,
      payModeNo: "",
      payModeDate: null,
      txnDate: moment().format("YYYY-MM-DD"),
      referenceNo: "",
      notes: "",
      accountID: null,
      status: "not_deducted",
      transactionLog: [],
      paymentLinked: false
    };
    this.drivers = [];
    this.carriers = [];
    this.ownerOperators = [];
    this.customers = [];
    this.vendors = [];
    this.employees = [];
    this.dateMinLimit = {
      year: 1950,
      month: 1,
      day: 1
    };
    this.date = new Date();
    this.futureDatesLimit = {
      year: this.date.getFullYear() + 30,
      month: 12,
      day: 31
    };
    this.accounts = [];
    this.payModeLabel = "";
    this.errors = {};
    this.response = "";
    this.hasError = false;
    this.hasSuccess = false;
    this.Error = "";
    this.Success = "";
    this.submitDisabled = false;
    this.showModal = false;
  }

  ngOnInit() {
    this.subscription = this.listService.paymentSaveList.subscribe(res => {
      if (res.openFrom === "addForm") {
        this.addRecord();
      }
    });
    this.paymentID = this.route.snapshot.params["paymentID"];

    if (this.paymentID) {
      this.fetchPaymentDetails();
    }

    this.fetchDrivers();
    this.fetchCarriers();
    this.fetchOwnerOperators();
    this.fetchEmployee(); // this.fetchVendor();

    this.listService.fetchVendors();
    let vendorList = new Array();
    this.getValidVendors(vendorList);
    this.vendors = vendorList; // this.fetchCustomer();

    this.listService.fetchChartAccounts();
    this.accounts = this.listService.accountsList;
  }

  ngOnDestroy() {
    this.subscription.unsubscribe();
  }

  fetchDrivers() {
    this.apiService.getData(`drivers/get/all/active`).subscribe(result => {
      // this.drivers = result.Items;
      result.Items.forEach(element => {
        if (element.isDeleted === 0) {
          this.drivers.push(element);
        }
      });
    });
  }

  refreshAccount() {
    this.listService.fetchChartAccounts();
  }

  fetchCarriers() {
    this.apiService.getData(`contacts/get/type/carrier`).subscribe(result => {
      // this.carriers = result;
      result.forEach(element => {
        if (element.isDeleted === 0) {
          this.carriers.push(element);
        }
      });
    });
  }

  fetchOwnerOperators() {
    this.apiService.getData(`contacts/get/type/ownerOperator`).subscribe(result => {
      // this.ownerOperators = result;
      result.forEach(element => {
        if (element.isDeleted === 0) {
          this.ownerOperators.push(element);
        }
      });
    });
  }

  fetchEmployee() {
    this.apiService.getData(`contacts/get/all/employees`).subscribe(result => {
      this.employees = result;
    });
  }

  getValidVendors(vendorList) {
    let ids = [];
    this.listService.vendorList.forEach(element => {
      element.forEach(element2 => {
        if (element2.isDeleted === 0 && !ids.includes(element2.contactID)) {
          vendorList.push(element2);
          ids.push(element2.contactID);
        }
      });
    });
  }

  fetchCustomer() {
    this.apiService.getData(`contacts/get/list/customer`).subscribe(result => {
      this.customers = result;
    });
  }

  cancel() {
    this.location.back(); // <-- go back to previous location on cancel
  }

  addRecord() {
    this.submitDisabled = true;
    this.accountService.postData("advance", this.paymentData).subscribe({
      complete: () => {},
      error: err => {
        from(err.error).pipe(map(val => {
          val.message = val.message.replace(/".*"/, "This Field");
          this.errors[val.context.key] = val.message;
        })).subscribe({
          complete: () => {
            this.submitDisabled = false; // this.throwErrors();
          },
          error: () => {
            this.submitDisabled = false;
          },
          next: () => {}
        });
      },
      next: res => {
        this.submitDisabled = false;
        this.response = res;
        this.toaster.success("Advance payment added successfully.");
        let obj = {
          type: '',
          openFrom: ''
        };
        this.listService.triggerPaymentSave(obj);
        let payObj = {
          showModal: false,
          page: ""
        };
        this.listService.openPaymentChequeModal(payObj);
        this.cancel();
      }
    });
  }

  changePaymentMode(type) {
    let label = "";

    if (type == "cash") {
      label = "Cash";
    } else if (type == "cheque") {
      label = "Cheque";
    } else if (type == "eft") {
      label = "EFT";
    } else if (type == "credit_card") {
      label = "Credit Card";
    } else if (type == "debit_card") {
      label = "Debit Card";
    } else if (type == "demand_draft") {
      label = "Demand Draft";
    }

    this.payModeLabel = label;
    this.paymentData.payModeNo = null;
    this.paymentData.payModeDate = null;
  }

  fetchPaymentDetails() {
    this.accountService.getData(`advance/detail/${this.paymentID}`).subscribe(result => {
      this.paymentData = result[0];
      this.payModeLabel = this.paymentData.payMode.replace("_", " ");

      if (this.payModeLabel === "eft") {
        this.payModeLabel = this.payModeLabel.toUpperCase();
      }
    });
  }

  updateRecord() {
    this.submitDisabled = true;
    this.accountService.putData(`advance/${this.paymentID}`, this.paymentData).subscribe({
      complete: () => {},
      error: err => {
        from(err.error).pipe(map(val => {
          val.message = val.message.replace(/".*"/, "This Field");
          this.errors[val.context.key] = val.message;
        })).subscribe({
          complete: () => {
            this.submitDisabled = false; // this.throwErrors();
          },
          error: () => {
            this.submitDisabled = false;
          },
          next: () => {}
        });
      },
      next: res => {
        this.submitDisabled = false;
        this.response = res;
        this.toaster.success("Advance payment updated successfully.");
        this.cancel();
      }
    });
  }

  resetEntityVal() {
    this.paymentData.entityId = null;
  }

  showCheque() {
    this.showModal = true;
    let obj = {
      entityId: this.paymentData.entityId,
      chequeDate: this.paymentData.payModeDate,
      chequeAmount: this.paymentData.amount,
      type: "advancePayment",
      paymentTo: this.paymentData.paymentTo,
      chequeNo: this.paymentData.payModeNo,
      currency: this.paymentData.currency,
      showModal: this.showModal,
      fromDate: this.paymentData.txnDate,
      finalAmount: this.paymentData.amount,
      txnDate: this.paymentData.txnDate,
      advType: this.paymentData.advType
    };
    this.listService.openPaymentChequeModal(obj);
  }

}

