      if (res.openFrom === "addForm") {
        this.addRecord();
      }
    });
    this.fetchAccounts();
  }

  ngOnDestroy() {
    this.subscription.unsubscribe();
  }

  emptyPrevSelection() {
    this.settlements = [];
    this.advancePayments = [];
    this.expenses = [];
    this.paymentData.finalAmount = 0;
    this.paymentData.advTotal = 0;
    this.paymentData.expTotal = 0;
    this.dataMessage = Constants.NO_RECORDS_FOUND;
    this.dataMessageAdv = Constants.NO_RECORDS_FOUND;
    this.dataMessageExp = Constants.NO_RECORDS_FOUND;
  }

  getEntityData(type = "") {
    this.emptyPrevSelection();

    if (type === "driver") {
      this.carriers = [];
      this.ownerOperators = [];
      this.fetchDrivers();
    } else if (type === "carrier") {
      this.drivers = [];
      this.ownerOperators = [];
      this.fetchCarriers();
    } else if (type === "owner_operator") {
      this.drivers = [];
      this.carriers = [];
      this.fetchOwnerOperators();
    }
  }

  fetchDrivers() {
    this.apiService.getData(`drivers/get/all/active`).subscribe(result => {
      result.Items.forEach(element => {
        if (element.isDeleted === 0) {
          this.drivers.push(element);
        }
      });
    });
  }

  fetchCarriers() {
    this.apiService.getData("contacts/get/type/carrier").subscribe(result => {
      result.forEach(element => {
        if (element.isDeleted === 0) {
          this.carriers.push(element);
        }
      });
    });
  }

  fetchOwnerOperators() {
    this.apiService.getData(`contacts/get/type/ownerOperator`).subscribe(result => {
      result.forEach(element => {
        if (element.isDeleted === 0) {
          this.ownerOperators.push(element);
        }
      });
    });
  }

