        _this.dataMessage = Constants.FETCHING_DATA;
        _this.searchDisabled = true;

        _this.fetchAdvancePayments();

        const result = yield _this.accountService.getData(`settlement/expenses/entity/${_this.paymentData.entityId}?from=${_this.paymentData.fromDate}&to=${_this.paymentData.toDate}&type=${_this.paymentData.paymentTo}&curr=${_this.paymentData.currency}`).toPromise(); // .subscribe((result: any) => {

        if (result.length === 0) {
          _this.dataMessage = Constants.NO_RECORDS_FOUND;
        }

