export class AddEmployeePaymentComponent {
  constructor(listService, route, location, router, toaster, accountService, apiService, httpClient, countryStateCity) {
    this.listService = listService;
    this.route = route;
    this.location = location;
    this.router = router;
    this.toaster = toaster;
    this.accountService = accountService;
    this.apiService = apiService;
    this.httpClient = httpClient;
    this.countryStateCity = countryStateCity;
    this.dataMessage = Constants.NO_RECORDS_FOUND;
    this.paymentData = {
      currency: "",
      entityId: null,
      txnDate: moment().format("YYYY-MM-DD"),
      paymentNo: "",
      payroll: {
        type: null,
        amount: 0,
        hours: 0,
        perHour: 0
      },
      fromDate: null,
      toDate: null,
      accountID: null,
      payMode: null,
      payModeNo: "",
      payModeDate: null,
      settledAmount: 0,
      vacPayPer: 0,
      vacPayAmount: 0,
      addition: [],
      deduction: [],
      paymentTotal: 0,
      additionTotal: 0,
      deductionTotal: 0,
      subTotal: 0,
      taxdata: {
        payPeriod: null,
        stateCode: null,
        federalCode: "claim_code_1",
        provincialCode: null,
        cpp: 0,
        ei: 0,
        federalTax: 0,
        provincialTax: 0,
        emplCPP: 0,
        emplEI: 0
      },
      taxes: 0,
      advance: 0,
      finalTotal: 0,
      advancePayIds: [],
      advData: [],
      transactionLog: []
    };
    this.dateMinLimit = {
      year: 1950,
      month: 1,
      day: 1
    };
    this.date = new Date();
    this.futureDatesLimit = {
      year: this.date.getFullYear() + 30,
      month: 12,
      day: 31
    };
    this.employees = [];
    this.empDetails = {
      paymentDetails: {
        payrollType: "",
        payrollRateUnit: "",
        payrollRate: ""
      }
    };
    this.additionRowData = {
      eventDate: null,
      chargeName: "",
      desc: "",
      amount: "",
      currency: ""
    };
    this.deductionRowData = {
      eventDate: null,
      chargeName: "",
      desc: "",
      amount: "",
      currency: ""
    };
    this.payModeLabel = "";
    this.errors = {};
    this.response = "";
    this.hasError = false;
    this.hasSuccess = false;
    this.Error = "";
    this.Success = "";
    this.submitDisabled = false;
    this.taxErr = "";
    this.advErr = "";
    this.lastAdded = {
      paymentNo: "",
      additionTotal: "",
      advance: "",
      deductionTotal: "",
      entityId: "",
      finalTotal: "",
      payMode: "",
      payModeDate: "",
      payModeNo: "",
      taxes: "",
      txnDate: ""
    };
    this.lastExist = false;
    this.empdetail = {
      companyName: "",
      contactID: "",
      firstName: "",
      lastName: "",
      userAccount: {
        department: "",
        designation: ""
      }
    };
    this.advancePayments = [];
    this.accList = [];
    this.payPeriods = [];
    this.states = [];
    this.claimCodes = [];
    this.provincalClaimCodes = [];
    this.showModal = false;
    this.employeesObj = {};
  }

  ngOnInit() {
    var _this = this;

    return _asyncToGenerator(function* () {
      _this.subscription = _this.listService.paymentSaveList.subscribe(res => {
        if (res.openFrom === "addForm") {
          _this.addRecord();
        }
      });
      _this.paymentID = _this.route.snapshot.params["paymentID"];

      if (_this.paymentID) {
        _this.fetchPaymentDetail();
      }

      _this.fetchEmployees();

      _this.listService.fetchChartAccounts();

      _this.accounts = _this.listService.accountsList;

      _this.fetchPayPeriods();

      yield _this.getStates();

      _this.fetchClaimCodes();
    })();
  }

  ngOnDestroy() {
    this.subscription.unsubscribe();
  }

  cancel() {
    this.location.back(); // <-- go back to previous location on cancel
  }

  fetchEmployees() {
    this.apiService.getData(`contacts/get/all/employees`).subscribe(result => {
      this.employees = result;
      this.employeesObj = result.reduce((a, b) => {
        return a[b["contactID"]] = b["isDeleted"] == 1 ? b["firstName"] + " " + b["lastName"] + "  - Deleted" : b["firstName"] + " " + b["lastName"], a;
      }, {});
    });
  }

  fetchAccounts() {
    this.accountService.getData(`chartAc/fetch/list`).subscribe(res => {
      this.accounts = res;
    });
  }

  refreshAccount() {
    this.listService.fetchChartAccounts();
  }

  fetchEmployeDetail() {
    this.paymentData.payroll.type = null;
    this.paymentData.payroll.amount = 0;
    this.paymentData.payroll.perHour = 0;
    this.paymentData.paymentTotal = 0;
    this.paymentData.settledAmount = 0;
    this.paymentData.finalTotal = 0;
    this.paymentData.subTotal = 0;
    this.paymentData.advance = 0;

    if (this.paymentData.entityId) {
      this.apiService.getData(`contacts/detail/${this.paymentData.entityId}`).subscribe(result => {
        this.empDetails = result.Items[0];
        let paymentInfo = this.empDetails.paymentDetails;
        this.paymentData.currency = paymentInfo.payrollRateUnit ? paymentInfo.payrollRateUnit : "CAD";

        if (paymentInfo.payrollType === "Hours") {
          this.paymentData.payroll.type = "hourly";
          this.paymentData.payroll.amount = Number(paymentInfo.payrollRate) * Number(this.paymentData.payroll.hours);
          this.paymentData.payroll.perHour = Number(paymentInfo.payrollRate);
        } else if (paymentInfo.payrollType === "Flat") {
          this.paymentData.payroll.type = "flat";
          this.paymentData.payroll.amount = Number(paymentInfo.payrollRate); // this.paymentData.paymentTotal = this.paymentData.payroll.amount;

          this.paymentData.settledAmount = this.paymentData.payroll.amount;
          this.paymentData.paymentTotal = Number(this.paymentData.settledAmount) + Number(this.paymentData.vacPayAmount);
          this.paymentData.finalTotal = this.paymentData.payroll.amount;
          this.paymentData.subTotal = this.paymentData.payroll.amount;
        }
      });
      this.fetchLastAdded();
      this.fetchPayments();
      this.calculatePayroll();
    }
  }

  fetchPayments() {
    if (this.paymentData.fromDate && this.paymentData.toDate) {
      this.fetchAdvancePayments();
    }
  }

  EmpRateCalc() {
    this.paymentData.payroll.amount = Number(this.paymentData.payroll.perHour) * Number(this.paymentData.payroll.hours); // this.paymentData.paymentTotal = this.paymentData.payroll.amount;

    this.paymentData.settledAmount = this.paymentData.payroll.amount;
    this.paymentData.paymentTotal = Number(this.paymentData.settledAmount) + Number(this.paymentData.vacPayAmount);
    this.calculateFinalTotal();
  }

  empFlatRate() {
    this.paymentData.payroll.amount = Number(this.paymentData.payroll.amount); // this.paymentData.paymentTotal = this.paymentData.payroll.amount;

    this.paymentData.settledAmount = this.paymentData.payroll.amount;
    this.paymentData.paymentTotal = Number(this.paymentData.settledAmount) + Number(this.paymentData.vacPayAmount);
    this.calculateFinalTotal();
    this.calculatePayroll();
  }

  addAdditionalExp() {
    if (this.additionRowData.eventDate !== null && this.additionRowData.chargeName !== "" && this.additionRowData.amount !== "" && this.paymentData.currency !== "") {
      this.additionRowData.currency = this.paymentData.currency;
      this.additionRowData.amount = Number(this.additionRowData.amount);
      this.paymentData.addition.push(this.additionRowData);
      this.additionRowData = {
        eventDate: null,
        chargeName: "",
        desc: "",
        amount: "",
        currency: this.paymentData.currency
      };
      this.calculateAddTotal();
    }
  }

  adddeductionExp() {
    if (this.deductionRowData.eventDate != null && this.deductionRowData.chargeName != "" && this.deductionRowData.amount != "" && this.paymentData.currency !== "") {
      this.deductionRowData.currency = this.paymentData.currency;
      this.deductionRowData.amount = Number(this.deductionRowData.amount);
      this.paymentData.deduction.push(this.deductionRowData);
      this.deductionRowData = {
        eventDate: null,
        chargeName: "",
        desc: "",
        amount: "",
        currency: this.paymentData.currency
      };
      this.calculateDedTotal();
    }
  }

  calculateAddTotal() {
    this.paymentData.additionTotal = 0;

    for (let i = 0; i < this.paymentData.addition.length; i++) {
      const element = this.paymentData.addition[i];
      this.paymentData.additionTotal += parseFloat(element.amount);
    }

    this.calculateFinalTotal();
  }

  calculateDedTotal() {
    this.paymentData.deductionTotal = 0;

    for (let i = 0; i < this.paymentData.deduction.length; i++) {
      const element = this.paymentData.deduction[i];
      this.paymentData.deductionTotal += parseFloat(element.amount);
    }

    this.calculateFinalTotal();
  }

  delRow(index, type) {
    if (type === "additional") {
      this.paymentData.addition.splice(index, 1);
      this.calculateAddTotal();
    } else {
      this.paymentData.deduction.splice(index, 1);
      this.calculateDedTotal();
    }
  }

  changePaymentMode(type) {
    let label = "";

    if (type == "cash") {
      label = "Cash";
      this.paymentData.payModeNo = "";
    } else if (type == "cheque") {
      label = "Cheque";
      this.paymentData.payModeNo = "";
    } else if (type == "eft") {
      label = "EFT";
      this.paymentData.payModeNo = "";
    } else if (type == "credit_card") {
      label = "Credit Card";
      this.paymentData.payModeNo = "";
    } else if (type == "debit_card") {
      label = "Debit Card";
      this.paymentData.payModeNo = "";
    } else if (type == "demand_draft") {
      label = "Demand Draft";
      this.paymentData.payModeNo = "";
    }

    this.payModeLabel = label;
    this.paymentData.payModeDate = null;
  }

  showAcModal() {
    $("#addAccountModal").modal("show");
  }

  addRecord() {
    if (this.paymentData.settledAmount <= 0) {
      this.toaster.error("Please enter valid amount");
      return false;
    }

    this.submitDisabled = true;
    this.accountService.postData("employee-payments", this.paymentData).subscribe({
      complete: () => {},
      error: err => {
        from(err.error).pipe(map(val => {
          val.message = val.message.replace(/".*"/, "This Field");
          this.errors[val.context.key] = val.message;
        })).subscribe({
          complete: () => {
            this.submitDisabled = false; // this.throwErrors();
          },
          error: () => {
            this.submitDisabled = false;
          },
          next: () => {}
        });
      },
      next: res => {
        this.submitDisabled = false;
        this.response = res;
        this.toaster.success("Employee payment added successfully.");
        let obj = {
          type: '',
          openFrom: ''
        };
        this.listService.triggerPaymentSave(obj);
        let payObj = {
          showModal: false,
          page: ""
        };
        this.listService.openPaymentChequeModal(payObj);
        this.cancel();
      }
    });
  }

  calculateFinalTotal() {
    this.paymentData.subTotal = Number(this.paymentData.subTotal);
    this.paymentData.taxes = Number(this.paymentData.taxes);
    this.paymentData.advance = Number(this.paymentData.advance);
    this.paymentData.settledAmount = Number(this.paymentData.settledAmount);
    this.paymentData.additionTotal = Number(this.paymentData.additionTotal);
    this.paymentData.deductionTotal = Number(this.paymentData.deductionTotal);
    this.paymentData.paymentTotal = Number(this.paymentData.vacPayAmount) + Number(this.paymentData.settledAmount);
    this.paymentData.paymentTotal = Number(this.paymentData.paymentTotal);
    this.paymentData.subTotal = this.paymentData.paymentTotal + this.paymentData.additionTotal - this.paymentData.deductionTotal;
    this.paymentData.finalTotal = this.paymentData.subTotal - this.paymentData.taxes - this.paymentData.taxdata.cpp - this.paymentData.taxdata.ei - this.paymentData.advance;
    this.paymentData.subTotal = Number(this.paymentData.subTotal.toFixed(2));
    this.paymentData.finalTotal = Number(this.paymentData.finalTotal.toFixed(2));
  }

  fetchPaymentDetail() {
    this.accountService.getData(`employee-payments/detail/${this.paymentID}`).subscribe(result => {
      this.paymentData = result[0];
      this.submitDisabled = true;
      this.assignProvincalCode();
    });
  }

  checkInput(type) {
    if (type == "tax") {
      if (Number(this.paymentData.taxes) > Number(this.paymentData.subTotal)) {
        this.taxErr = "Tax amount should be less than sub total";
        this.submitDisabled = true;
      } else {
        this.taxErr = "";
        this.submitDisabled = false;
      }
    } else if (type == "advance") {
      if (Number(this.paymentData.advance) > Number(this.paymentData.subTotal)) {
        this.advErr = "Advance amount should be less than sub total";
        this.submitDisabled = true;
      } else {
        this.advErr = "";
        this.submitDisabled = false;
      }
    }
  }

  fetchLastAdded() {
    this.lastExist = false;
    this.accountService.getData(`employee-payments/last/added/${this.paymentData.entityId}`).subscribe(result => {
      if (result.length > 0) {
        this.lastExist = true;
        this.fetchEmpDetail(result[0].entityId);
      }

      this.lastAdded = result[0];
    });
