  fetchVehicles() {
    if (this.vehicles.length === 0) {
      this.vehicleMessage = Constant.FETCHING_DATA;
      this.apiService.getData("vehicles").subscribe(result => {
        // this.vehicles = result.Items;
        if (result.Items.length === 0) {
          this.vehicleMessage = Constant.NO_RECORDS_FOUND;
        }

        result.Items.forEach(element => {
          if (element.isDeleted === 0) {
            this.vehicles = [...this.vehicles, element];
          }
        });
        this.vehiclesObjects = result.Items.reduce((a, b) => {
          return a[b["vehicleID"]] = b["isDeleted"] == 1 ? b["vehicleIdentification"] + "  - Deleted" : b["vehicleIdentification"], a;
        }, {});
      });
    }
  }

